import { useReducer } from 'react'
import './MainForm.css'
import PersonalInfo from './PersonalInfo'
import Plan from './Plan'
import AddOn from './AddOn'
import Summary from './Summary'
import End from './End'



function Form() {
  function formReducer(state, action) {
        switch (action.type) {
            case "nextpage": return { ...state, page: state.page + 1 }

            case "prevpage": return { ...state, page: state.page - 1 }

            case "arcadeRadioSelect": return { ...state, plan: state.plan = "Arcade" , MonthlyFinalPrice:state.MonthlyFinalPrice=9,YearlyFinalPrice:state.YearlyFinalPrice=90  }

            case "advancedRadioSelect": return { ...state, plan: state.plan = "Advanced", MonthlyFinalPrice:state.MonthlyFinalPrice=12,YearlyFinalPrice:state.YearlyFinalPrice=120 }

            case "proRadioSelect": return { ...state, plan: state.plan = "Pro", MonthlyFinalPrice:state.MonthlyFinalPrice=15,YearlyFinalPrice:state.YearlyFinalPrice=150 }

            case "MonthlyDuration": return { ...state, switchDuration: state.switchDuration = "" ,duration:state.duration="Monthly",arcadeprice:state.arcadeprice=9,advancedPrice:state.advancedPrice=12,proPrice:state.proPrice=15,AddOnOnlinePrice:state.AddOnOnlinePrice=1,AddOnProfilePrice:state.AddOnProfilePrice=2,AddOnStoragePrice:state.AddOnStoragePrice=2 }

            case "YearlyDuration": return { ...state, switchDuration: state.switchDuration = "right", duration: state.duration = "Yearly", arcadeprice:state.arcadeprice=90,advancedPrice:state.advancedPrice=120,proPrice:state.proPrice=150,AddOnOnlinePrice:state.AddOnOnlinePrice=10,AddOnProfilePrice:state.AddOnProfilePrice=20,AddOnStoragePrice:state.AddOnStoragePrice=20 }

            case "change":return{...state,page:state.page=1}
            default: return state
       }
  }
    const [state, dispatch] = useReducer(formReducer, {
        page: 0,
        fullname: "",
        mail: "",
        phone: "",
        plan: "",
        duration: "Monthly",
        switchDuration: "",
        chooseAddOn: "",
        arcadeprice: 9,
        advancedPrice: 12,
        proPrice:15,
        MonthlyFinalPrice: 0,
        YearlyFinalPrice: 0,
        AddOnOnlinePrice: 1,
        AddOnStoragePrice: 2,
        AddOnProfilePrice: 2,
        AddOnOnlineDisplay:"d-none",
        AddOnStorageDisplay:"d-none",
        AddOnProfileDisplay:"d-none",
    })


    function DisplayPage() {
        if (state.page === 0) {
            return <PersonalInfo state={state } Dispatch={dispatch} />
        } else if (state.page === 1) {
            return <Plan state={state} dispatch={dispatch} />
        } else if (state.page === 2) {
            return <AddOn state={state} dispatch={dispatch}/>
        } else if (state.page === 3) {
            return <Summary state={state} dispatch={dispatch}/>
        } else {
            return <End/>
        }

    }

    return (
        <div  className="container-fluid  ">
            <div  className="row vh-100 p-md-3">
                <ul className="side col-md-4 p-md-5 ">
                    <li className='Main-Box d-flex align-items-center mb-md-4'>
                        <div className={`number ${state.page===0?"active":""}  d-flex align-items-center justify-content-center p-md-3 me-md-3`}>1</div>
                        <div className='text'>
                            <span className='step'>Step 1</span>
                            <div className='info'>Your Info</div>
                        </div>
                    </li>
                     <li className='Main-Box d-flex align-items-center mb-md-4'>
                        <div className={`number ${state.page===1?"active":""} d-flex align-items-center justify-content-center p-md-3 me-md-3`}>2</div>
                        <div className='text'>
                            <span className='step'>Step 2</span>
                            <div className='info'>Select Plan</div>
                        </div>
                    </li>
                     <li className='Main-Box d-flex align-items-center mb-md-4'>
                        <div className={`number ${state.page===2?"active":""} d-flex align-items-center justify-content-center p-md-3 me-md-3`}>3</div>
                        <div className='text'>
                            <span className='step'>Step 3</span>
                            <div className='info'>ADD-ONS</div>
                        </div>
                    </li>
                     <li className='Main-Box d-flex align-items-center mb-md-4'>
                        <div className={`number ${state.page===3 || state.page===4?"active":""} d-flex align-items-center justify-content-center p-md-3 me-md-3`}>4</div>
                        <div className='text'>
                            <span className='step'>Step 4</span>
                            <div className='info'>SUMMARY</div>
                        </div>
                    </li>
                </ul>
                <div className='col-md-8 position-relative'>
                    <DisplayPage />
                     <div className='col-md-12 d-flex justify-content-between align-items-center p-5 control position-absolute bottom-0'>
                        <button onClick={()=>dispatch({type:"prevpage"})} className='back col-2'>{state.page===0?"":"Go Back" }</button>
                    <button type='submit' onClick={()=>dispatch({type:"nextpage"})} className='next col-2 text-center py-md-3'>Next Step</button>
                </div>
                </div>

            </div>
     </div>
    )
}
export default Form
